import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

function Banner() {
  const settings = {
    dots: true,           // Hiển thị chấm tròn điều hướng
    infinite: true,       // Vòng lặp vô hạn
    speed: 500,           // Thời gian chuyển slide
    slidesToShow: 1,      // Hiển thị 1 slide
    slidesToScroll: 1,    // Cuộn từng slide
    autoplay: true,       // Tự động chạy
    autoplaySpeed: 3000,  // 3 giây đổi slide
  };

  return (
    <div style={{ width: "100%", maxWidth: "1200px", margin: "20px auto" }}>
      <Slider {...settings}>
        <div>
          <img
            src="https://via.placeholder.com/1200x400?text=Slide+1"
            alt="Slide 1"
            style={{ width: "100%", borderRadius: "10px" }}
          />
        </div>
        <div>
          <img
            src="https://via.placeholder.com/1200x400?text=Slide+2"
            alt="Slide 2"
            style={{ width: "100%", borderRadius: "10px" }}
          />
        </div>
        <div>
          <img
            src="https://via.placeholder.com/1200x400?text=Slide+3"
            alt="Slide 3"
            style={{ width: "100%", borderRadius: "10px" }}
          />
        </div>
      </Slider>
    </div>
  );
}

export default Banner;
